const MainNews = () => {

    const router = useRouter()

    const [ openCommentDrawer, setOpenCommentDrawer ] = useState(false)

    const openCommentDrawerHandler = () => {
        setOpenCommentDrawer(true)
    }

    const closeCommentDrawer = () => {
        setOpenCommentDrawer(false)
    }

    return (
        <>
       { openCommentDrawer &&  <AddComment closeHandler={closeCommentDrawer} /> } 
        <div className={styles.mainNewsContainer}>
            <div className={styles.recommendedNewsContainer}>
                { newsData.map((news,index)=>{
                    let marginTop="40px";
                    if(index==0){
                        marginTop=0
                    }
                    return (
                <div style={{marginTop:marginTop}}  className={styles.recommendedNewsBox}>
                    <img className={styles.recommendedImage} src={news.img} alt="" />
                    <div className={styles.recommendedContent}>
                        <div className={styles.recommendedTitle}>{news.title}</div>
                        <div className={styles.dateContainer}>
                        <DateRangeIcon style={{ fontSize: "16px", color: "gray" }} />
                        <div className={styles.recommendedDate}>{news.date}</div>
                        </div>
                        <div className={styles.recommendedDesc}>{news.desc.substring(0,400)}...</div>
                        <div className={styles.interactions}>
                            <div className={styles.interactionsBox}>
                            <div className={styles.likeCount}>1</div>
                            <ThumbUpOutlinedIcon  style={{fontSize:20,color:"gray"}} />
                            </div>
                            <div onClick={openCommentDrawerHandler} className={styles.interactionsBox}>
                            <div className={styles.commentCount}>100</div>
                            <CommentOutlinedIcon  style={{fontSize:20,color:"gray"}} />
                            </div>
                        </div>
                        <div onClick={()=>router.push('/news/1')} className={styles.recommendbuttonContainer}>
                            Read more
                            <ArrowForwardIosIcon className={styles.arrowIcon}/>
                        </div>
                    </div>
                </div>
                    )
                }) }
            </div>

            <div className={styles.relatedTopics}>
                        <div className={styles.relatedNewsTitle}>Top News</div>
                        <div className={styles.divider}>
                            <div className={styles.dividerColor}></div>
                        </div>
                        {relatedData.map((data, index) => {
                            return (
                                <div onClick={()=>router.push('/news/1')} className={styles.relatedNewsBox} key={index} >
                                    <img src={data.img} className={styles.relatedNewsImage} />
                                    <div>
                                        <div className={styles.relatedNewsTitle}>{data.title}</div>
                                        <div style={{ display: "flex", gap: "4px", alignItems: "center", marginTop: "12px" }} >
                                            <DateRangeIcon style={{ fontSize: "16px", color: "gray" }} />
                                            <div className={styles.newsDate}>14 Jul, 2022 13:08 PM</div>
                                        </div>
                                    </div>
                                </div>
                            )
                        })}
                    </div>
        </div>
        </>
    )
}

export default MainNews
